/*
  app.js – Neurosynth frontend demo
  - Implements three API calls using fetch
  - Pretty rendering with Tailwind-friendly HTML
  - Clear loading/error states
*/

const API_BASE = 'https://mil.psy.ntu.edu.tw:5000';

// ---------- Utilities ----------
const $ = (sel) => document.querySelector(sel);

function showLoading(el, text = '載入中…') {
  el.innerHTML = `
    <div class="flex items-center gap-2 text-slate-600">
      <svg class="animate-spin h-5 w-5 text-brand-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
        <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
        <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z"></path>
      </svg>
      <span>${text}</span>
    </div>`;
}

function showError(el, err) {
  const msg = typeof err === 'string' ? err : (err && err.message) || String(err);
  el.innerHTML = `
    <div class="rounded-lg border border-red-200 bg-red-50 text-red-700 p-4">
      <div class="font-semibold mb-1">發生錯誤</div>
      <pre class="whitespace-pre-wrap text-sm">${escapeHtml(msg)}</pre>
      <div class="mt-2 text-xs text-red-600/80">常見原因：CORS 未允許、網路問題、或伺服器錯誤</div>
    </div>`;
}

function escapeHtml(s) {
  return String(s)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#39;');
}

function renderList(el, items) {
  if (!Array.isArray(items) || items.length === 0) {
    el.innerHTML = '<div class="text-slate-500">沒有資料</div>';
    return;
  }
  const lis = items
    .map((x) => `<li class="py-2 border-b last:border-b-0 border-slate-100">${escapeHtml(String(x))}</li>`) 
    .join('');
  el.innerHTML = `
    <ul class="divide-y divide-slate-100">${lis}</ul>`;
}

function renderTags(el, items) {
  if (!Array.isArray(items) || items.length === 0) {
    el.innerHTML = '<div class="text-slate-500">沒有相關術語</div>';
    return;
  }
  const chips = items
    .map((x) => `<span class="inline-flex items-center px-2.5 py-1 rounded-full bg-brand-50 text-brand-700 border border-brand-200">${escapeHtml(String(x))}</span>`) 
    .join(' ');
  el.innerHTML = `<div class="flex flex-wrap gap-2">${chips}</div>`;
}

function renderStudies(el, data) {
  // Try to support various shapes: array of objects, or object with results
  let studies = [];
  if (Array.isArray(data)) studies = data;
  else if (data && Array.isArray(data.results)) studies = data.results;
  else if (data && Array.isArray(data.studies)) studies = data.studies;

  if (!Array.isArray(studies) || studies.length === 0) {
    el.innerHTML = '<div class="text-slate-500">沒有符合的研究</div>';
    return;
  }

  // Determine columns
  const columns = ['id', 'title', 'name', 'score'];

  const thead = `<thead class="bg-slate-50 text-slate-700"><tr>${columns
    .map((c) => `<th class="px-3 py-2 text-left font-medium">${escapeHtml(c)}</th>`)
    .join('')}</tr></thead>`;

  const rows = studies
    .map((s) => {
      const cells = columns
        .map((c) => `<td class="px-3 py-2 text-sm text-slate-700">${escapeHtml(
          (s && (s[c] ?? s[c?.toUpperCase?.() || c])) ?? ''
        )}</td>`) 
        .join('');
      return `<tr class="border-b last:border-b-0 border-slate-100">${cells}</tr>`;
    })
    .join('');

  el.innerHTML = `
    <div class="overflow-x-auto">
      <table class="min-w-full text-sm">${thead}<tbody>${rows}</tbody></table>
    </div>`;
}

async function fetchJson(url) {
  const resp = await fetch(url, { headers: { 'Accept': 'application/json, text/plain;q=0.8, */*;q=0.5' } });
  const ct = resp.headers.get('content-type') || '';
  if (!resp.ok) {
    const text = await resp.text().catch(() => '');
    throw new Error(`HTTP ${resp.status} ${resp.statusText}\n${text}`);
  }
  if (ct.includes('application/json')) {
    return resp.json();
  }
  // Fallback: try text then JSON parse
  const text = await resp.text();
  try { return JSON.parse(text); } catch { return { raw: text }; }
}

// ---------- Wire up UI ----------
window.addEventListener('DOMContentLoaded', () => {
  // Quick helpers for demo buttons to fill inputs
  document.querySelectorAll('[data-fill]').forEach((btn) => {
    btn.addEventListener('click', () => {
      const input = document.querySelector(btn.getAttribute('data-fill'));
      if (input) input.value = btn.getAttribute('data-value') || '';
      input?.focus();
    });
  });

  // A. All terms
  const termsOut = $('#terms-out');
  $('#btn-terms')?.addEventListener('click', async () => {
    showLoading(termsOut, '查詢所有術語…');
    try {
      const data = await fetchJson(`${API_BASE}/terms`);
      // Expecting array
      renderList(termsOut, Array.isArray(data) ? data : (data?.terms || data));
    } catch (err) {
      showError(termsOut, err);
    }
  });

  // B. Related terms for a given t1
  const relatedOut = $('#related-out');
  $('#btn-related')?.addEventListener('click', async () => {
    const t1 = $('#input-t1').value.trim();
    if (!t1) {
      relatedOut.innerHTML = '<div class="text-amber-700 bg-amber-50 border border-amber-200 rounded-lg p-3">請先輸入術語</div>';
      return;
    }
    showLoading(relatedOut, '查詢相關術語…');
    try {
      const data = await fetchJson(`${API_BASE}/terms/${encodeURIComponent(t1)}`);
      // Try to read array-like
      const items = Array.isArray(data) ? data : (data?.related || data?.terms || data);
      renderTags(relatedOut, items);
    } catch (err) {
      showError(relatedOut, err);
    }
  });

  // C. Logic query studies
  const queryOut = $('#query-out');
  $('#btn-query')?.addEventListener('click', async () => {
    const q = $('#input-q').value.trim();
    if (!q) {
      queryOut.innerHTML = '<div class="text-amber-700 bg-amber-50 border border-amber-200 rounded-lg p-3">請先輸入查詢字串</div>';
      return;
    }
    showLoading(queryOut, '執行查詢…');
    try {
      const url = `${API_BASE}/query/${encodeURIComponent(q)}/studies`;
      const data = await fetchJson(url);
      renderStudies(queryOut, data);
    } catch (err) {
      showError(queryOut, err);
    }
  });
});

