        .map((c) => `<td class="px-3 py-2 text-sm text-slate-700">${escapeHtml(
          (s && (s[c] ?? s[c?.toUpperCase?.() || c])) ?? ''
        )}</td>`) 
        .join('');
  return `<tr class="border-b last:border-b-0 border-gray-100">${cells}</tr>`;
    })
    .join('');

  el.innerHTML = `
    <div class="overflow-x-auto">
      <table class="min-w-full text-sm">${thead}<tbody>${rows}</tbody></table>
    </div>`;
}

async function fetchJson(url) {
