  const thead = `<thead class="bg-gray-50 text-gray-700"><tr>${columns
    .map((c) => `<th class="px-3 py-2 text-left font-medium">${escapeHtml(c.label)}</th>`)
    .join('')}</tr></thead>`;

  const rows = studies
    .map((s) => {
      const cells = columns
        .map((c) => {
