          return `<td class="px-3 py-2 text-sm text-gray-700 ${behavior}">${escapeHtml(val)}</td>`;
        })
        .join('');
      return `<tr class="border-b last:border-b-0 border-gray-100">${cells}</tr>`;
    })
    .join('');

  el.innerHTML = `
    <div class="mb-3 text-sm text-gray-600">Count: ${studies.length}</div>
    <div class="overflow-x-auto">
      <table class="min-w-full table-fixed text-sm">${colgroup}${thead}<tbody>${rows}</tbody></table>
    </div>`;
}

async function fetchJson(url) {
  const resp = await fetch(url, { headers: { 'Accept': 'application/json, text/plain;q=0.8, */*;q=0.5' } });
  const ct = resp.headers.get('content-type') || '';
  if (!resp.ok) {
